changeSet.sql:
DROP INDEX IF EXISTS ${database.defaultSchemaName}.error_logs_unique_idx;
WITH duplicates AS (
SELECT ctid, ROW_NUMBER() OVER (PARTITION BY request_id, instance_id, md5(error_msg::text)
ORDER BY ctid) AS rn
FROM ${database.defaultSchemaName}.errors
)
DELETE FROM ${database.defaultSchemaName}.errors
WHERE ctid IN (
SELECT ctid FROM duplicates WHERE rn > 1
);
CREATE UNIQUE INDEX IF NOT EXISTS error_logs_unique_idx
ON ${database.defaultSchemaName}.errors (request_id, instance_id, md5(error_msg));
