--
-- Name: instance_holdings_item_view; Type: VIEW; Schema: oaitest_mod_inventory_storage; Owner: folio_admin
--

CREATE VIEW oaitest_mod_inventory_storage.instance_holdings_item_view AS
 SELECT instance.id,
    jsonb_build_object('instanceId', instance.id, 'instance', instance.jsonb, 'holdingsRecords', ( SELECT jsonb_agg(holdings_record.jsonb) AS jsonb_agg
           FROM oaitest_mod_inventory_storage.holdings_record
          WHERE (holdings_record.instanceid = instance.id)), 'items', ( SELECT jsonb_agg(item.jsonb) AS jsonb_agg
           FROM (oaitest_mod_inventory_storage.holdings_record hr
             JOIN oaitest_mod_inventory_storage.item ON (((item.holdingsrecordid = hr.id) AND (hr.instanceid = instance.id))))), 'isBoundWith', ( SELECT (EXISTS ( SELECT 1
                   FROM ((oaitest_mod_inventory_storage.bound_with_part bw
                     JOIN oaitest_mod_inventory_storage.item it ON ((it.id = bw.itemid)))
                     JOIN oaitest_mod_inventory_storage.holdings_record hr ON ((hr.id = bw.holdingsrecordid)))
                  WHERE (hr.instanceid = instance.id)
                 LIMIT 1)) AS "exists")) AS jsonb
   FROM oaitest_mod_inventory_storage.instance;


ALTER TABLE oaitest_mod_inventory_storage.instance_holdings_item_view OWNER TO folio_admin;

