INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('b13f10e7-778f-4755-a860-171238d0189e', '{"id": "b13f10e7-778f-4755-a860-171238d0189e", "hrid": "in00000000006", "tags": {"tagList": []}, "notes": [{"note": "Includes bibliographical references (pages 251-270) and index", "staffOnly": false, "instanceNoteTypeId": "86b6e817-e1bc-42fb-bab0-70e7547de6c1"}, {"note": "Introduction / Sylvain Belluc and Valerie Benejam -- Knowledge and identity in Joyce / Fran O''Rourke -- Intentionality and epiphany : Husserl, Joyce, and the problem of access / Jean-Baptiste Fournier -- Authors'' libraries and the extended mind : the case of Joyce''s books / Dirk Van Hulle -- Characters'' lapses and language''s past : etymology as cognitive tool in Joyce''s fiction / Sylvain Belluc -- Joyce and hypnagogia / Thomas Jackson Rice -- Spatialized thought : waiting as cognitive state in Dubliners / Caroline Morillot -- The invention of Dublin as \"naissance de la clinique\" : cognition and pathology in Dubliners / Benoit Tadie -- Cognition as drama : Stephehn Dedalus''s mental workshop in A Portrait of the Artist as Young Man / Andre Topia -- Joycean text/empathic reader : a modest contribution to literary neuroaesthetics / Pierre-Louis Patoine -- Configuring cognitive architecture : mind-reading and meta-representations in Ulysses / Lizzy Welby -- Hallucination and the text : \"Circe\" between narrative, epistemology, and neurosciences / Teresa Prudente -- \"[The] buzz in his braintree, the tic of His conscience\" : consciousness, language and the brain in Finnegans Wake / Annalisa Volpone", "staffOnly": false, "instanceNoteTypeId": "5ba8e385-0e27-462e-a571-ffa1fa34ea54"}, {"note": "\"This collection is the first book-length study to re-evaluate all of James Joyce''s major fictional works through the lens of cognitive studies. Cognitive Joyce presents Joyce''s relationship to the scientific knowledge and practices of his time and examines his texts in light of contemporary developments in cognitive and neuro-sciences. The chapters pursue a threefold investigation--into the author''s \"extended mind\" at work, into his characters'' complex and at times pathological perceptive and mental processes, and into the elaborate responses the work elicits as we perform the act of reading. This volume not only offers comprehensive overviews of the oeuvre, but also detailed close-readings that unveil the linguistic focus of Joyce''s drama of cognition.\"-- Back cover", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}], "title": "Cognitive Joyce / Sylvain Belluc, Valerie Benejam, editors.", "series": [{"value": "Cognitive studies in literature and performance."}], "source": "MARC", "_version": 2, "editions": [], "metadata": {"createdDate": "2023-06-30T13:53:10.402Z", "updatedDate": "2023-06-30T13:53:48.607Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "Joyce, James, 1882-1941--Psychology"}, {"value": "Joyce, James, 1882-1941--Stories, plots, etc"}, {"value": "Cognition in literature"}], "languages": ["eng"], "indexTitle": "Cognitive Joyce /", "identifiers": [{"value": "9783319719931 (hardcover ; acid-free paper)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "3319719939 (hardcover ; acid-free paper)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "9783319719948 (ebook)", "identifierTypeId": "fcca2643-406a-482a-b760-7a7f8aec640e"}, {"value": "3319719947 (ebook)", "identifierTypeId": "fcca2643-406a-482a-b760-7a7f8aec640e"}, {"value": "(OCoLC)1009062789", "identifierTypeId": "439bfbae-75bc-4f74-9fc7-b2a2d47ce3ef"}], "publication": [{"role": "Publication", "place": "Cham, Switzerland", "publisher": "Palgrave Macmillan", "dateOfPublication": "[2018]"}, {"dateOfPublication": "짤2018"}], "contributors": [{"name": "Belluc, Sylvain,", "primary": false, "contributorTypeText": "editor.", "co