INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('3e3dc4c6-d9d9-402c-991b-ad4880ca81f0', '{"id": "3e3dc4c6-d9d9-402c-991b-ad4880ca81f0", "hrid": "in00000000002", "tags": {"tagList": []}, "notes": [{"note": "Includes index", "staffOnly": false, "instanceNoteTypeId": "6a2533a7-4de2-4e64-8466-074c2fa9308c"}, {"note": "\"Amy Dempsey unravels the all-too-often daunting language of modern art by mapping the styles, schools and movements that help us understand modern and contemporary art, from Impressionism in the 19th century to Destination Art in the 21st. Using a practical and easy-to-navigate structure, Dempsey''s lucid writing and carefully selected artworks define sixty-eight essential groupings in western modern art.\"--From cover flap", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}], "title": "Modern art / Amy Dempsey.", "series": [], "source": "MARC", "_version": 2, "editions": [], "metadata": {"createdDate": "2023-06-30T13:53:09.640Z", "updatedDate": "2023-06-30T13:53:48.248Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "Art, Modern"}, {"value": "Art, Modern--20th century"}, {"value": "Art, Modern--21st century"}], "languages": ["eng"], "indexTitle": "Modern art /", "identifiers": [{"value": "9780500293225 (paperback)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "0500293228 (paperback)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "(OCoLC)1026387505", "identifierTypeId": "439bfbae-75bc-4f74-9fc7-b2a2d47ce3ef"}], "publication": [{"role": "Publication", "place": "New York, New York", "publisher": "Thames & Hudson", "dateOfPublication": "2018"}, {"dateOfPublication": "짤2018"}], "contributors": [{"name": "Dempsey, Amy, 1963-", "primary": true, "contributorTypeText": "author.", "contributorNameTypeId": "2b94c631-fca9-4892-a730-03ee529ffe2a"}], "instanceTypeId": "6312d172