INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('9919d667-3eb5-4135-bd61-fbf7c22f6a8b', '{"id": "9919d667-3eb5-4135-bd61-fbf7c22f6a8b", "hrid": "in00000000004", "tags": {"tagList": []}, "notes": [{"note": "This is the first study of the diary in French writing across the 20th century, as a genre which includes both fictional and non-fictional works. From the 1880s it became apparent to writers in France that their diaries - a supposedly private form of writing - would probably come to be published, strongly affecting the way their readers viewed their other published works, and their very persona as an author. More than any other, Andre Gide embraced the literary potential of the diary: the first part of this book follows his experimentation with the diary in the fictional works ''Les Cahiers d''Andre Walter'' and ''Paludes'', in his diary of the composition of his great novel, ''Le Journal des Faux-Monnayeurs,'' and in his monumental Journal 1889-1939", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}, {"note": "Includes bibliographical references (pages [229]-240) and index", "staffOnly": false, "instanceNoteTypeId": "86b6e817-e1bc-42fb-bab0-70e7547de6c1"}], "title": "Diaries real and fictional in twentieth-century French writing / Sam Ferguson.", "series": [{"value": "Oxford modern languages and literature monographs."}], "source": "MARC", "_version": 2, "editions": ["First edition."], "metadata": {"createdDate": "2023-06-30T13:53:09.671Z", "updatedDate": "2023-06-30T13:53:48.370Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "French literature--20th century--History and criticism"}, {"value": "Authors, French--20th century--Diaries"}, {"value": "Diaries in literature"}, {"value": "Diary fiction"}], "languages": ["eng"], "indexTitle": "Diaries real and fictional in twentieth-century French writing /", "identifiers": [{"valu