INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('98043f44-19f0-450c-9dc2-eaa61ecf9478', '{"id": "98043f44-19f0-450c-9dc2-eaa61ecf9478", "hrid": "in00000000021", "tags": {"tagList": []}, "notes": [{"note": "Includes bibliographical references and index", "staffOnly": false, "instanceNoteTypeId": "86b6e817-e1bc-42fb-bab0-70e7547de6c1"}, {"note": "\"Quiet men are dangerous\": civilian antecedents of the Union Leaguess -- \"There can be no neutrals in this war; only patriots or traitors\": the demand for public loyalty -- \"A fire of liberty burning upon the altar\": the Union Leagues arise amidst despair and disillusionment -- \"A refuge rather than a resort for loyalty\": Philadelphia, New York, and Boston lead the way -- \"We are learning to draw the line between treason and loyalty\": Union League ostracism and Democratic resentment -- \"This is the time for pamphleteers and essayists\": the pen begins to fight alongside the sword against copperhead dissent and violence -- \"The Loyal Leagues are really effecting public opinion\": the broad-based loyal leagues and \"no party now? -- \"Neutrality is allied to treason; indifference becomes a crime; and whoever is not with us is against us\": a Union League of America Council in every town -- \"We are not a partisan, yet we are a political organization\": women enter the fray as Midwest dissent boils over -- \"We are organizing our leagues and getting ready for the great fight of 1864\": an open arm of the Republican Party -- \"Once more rally around the flag, and your work will be complete\": a bitter and partisan election -- \"It is a fatal mistake to hold that this war is over because the fighting has ceased\": the Union League in Reconstruction", "staffOnly": false, "instanceNoteTypeId": "5ba8e385-0e27-462e-a571-ffa1fa34ea54"}, {"note": "\"This book in the Civil War in the North series examines the Union League movement and its influence on the Northern home front in the Civil War.\"--Provided by publish