INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('f5e93211-012b-47d2-aa02-243d146c7609', '{"id": "f5e93211-012b-47d2-aa02-243d146c7609", "hrid": "in00000000015", "tags": {"tagList": []}, "notes": [{"note": "Collection of texts partly published previously", "staffOnly": false, "instanceNoteTypeId": "6a2533a7-4de2-4e64-8466-074c2fa9308c"}, {"note": "\"Originally published as ''Die Pixel des Paul Cezanne und andere Blicke auf Kunstler'' by Verlag der Autoren, Frankfurt am Main, 2015\" -- Title page verso", "staffOnly": false, "instanceNoteTypeId": "6a2533a7-4de2-4e64-8466-074c2fa9308c"}, {"note": "I write, therefore I think -- For (not about) Ingmar Bergman -- On 30 July 2007: Ingmar Bergman and Michelangelo Antonioni -- My time with Antonioni -- Stills of an American dream: Edward Hopper -- About Peter Lindbergh -- Mann of the West: Anthony Mann -- Written on the wind: Douglas Sirk -- The storyteller: Samuel Fuller -- The time traveller: Manoel de Oliveira -- Speech for Pina: Pina Bausch -- Eulogy for James Nachtwey on the occasion of the Dresden Prize -- The lost paradise: Yasujiro Ozu -- On closer inspection: Andrew Wyeth -- A master craftswoman of reality: Barbara Klemm -- Getting to know Yohji: Yohji Yamamoto -- The pixels of Paul Cezanne..", "staffOnly": false, "instanceNoteTypeId": "5ba8e385-0e27-462e-a571-ffa1fa34ea54"}, {"note": "Translated from the German", "staffOnly": false, "instanceNoteTypeId": "7356cde5-ec6b-4961-9cb0-961c48a37af4"}, {"note": "\"The Pixels of Paul Cezanne is a collection of essays by Wim Wenders in which he presents his observations and reflections on the fellow artists who have influenced, shaped, and inspired him. How are they doing it? is the key questions Wenders asks as he looks at the dance work of Pina Bausch, the paintings of Cezanne, Edward Hopper of Andrew Wyeth, or the films of Ingmar Bergman, Michelanelo Antonioni, Ozu, Anthony Mann, Douglas Sirk, and Sam Fuller. He finds the answer by writing about them, trying to 