INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('575bd127-ab70-4e85-acf8-3f6e7eb7f32b', '{"id": "575bd127-ab70-4e85-acf8-3f6e7eb7f32b", "hrid": "in00000000026", "tags": {"tagList": []}, "notes": [{"note": "Poems", "staffOnly": false, "instanceNoteTypeId": "6a2533a7-4de2-4e64-8466-074c2fa9308c"}, {"note": "\"Power and sex take centre stage in Robin Richardson''s formidable third collection, Sit How You Want. Plane crashes and automobile mishaps are the backdrop for female narrators who grapple with terror, anxiety, and powerlessness: \"When I say I''m fine I mean the sky has opened / like an old wound under scurvy.\" In their grim wit, sinister straight talk, and sometimes violent bawdiness, Richardson''s poems work as counter-charms against the lingering trauma of abusive relationships, both familial and romantic. The book embodies a belief in poetry as an instrument of change, a tool for transforming pain into exuberant verbal energy: \"It is the thrill of ruination / makes us innovate.\"\"-- Provided by publisher", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}], "title": "Sit how you want / Robin Richardson.", "series": [], "source": "MARC", "_version": 2, "editions": [], "metadata": {"createdDate": "2023-06-30T13:53:29.826Z", "updatedDate": "2023-06-30T13:54:05.152Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "Women--Poetry"}, {"value": "Control (Psychology)--Poetry"}, {"value": "Control (Psychology)"}, {"value": "Women"}, {"value": "Poetry"}], "languages": ["eng"], "indexTitle": "Sit how you want /", "identifiers": [{"value": "1029481813", "identifierTypeId": "fc4e3f2a-887a-46e5-8057-aeeb271a4e56"}, {"value": "1029569116", "identifierTypeId": "fc4e3f2a-887a-46e5-8057-aeeb271a4e56"}, {"value": "1029645962", "identifierTypeId": "fc4e3f2a-887a-46e5-8057-aeeb271a4e56"}, {"value": "1035828399", "identifierTypeId": "fc4e3f2