INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('0736eb32-5a2c-4215-9212-6b7d518d059e', '{"id": "0736eb32-5a2c-4215-9212-6b7d518d059e", "hrid": "in00000000017", "tags": {"tagList": []}, "notes": [{"note": "\"A Distant Center moves award-winning fiction writer Ha Jin''s astounding poetic talent into the spotlight. Originally inscribed in Chinese and rendered into English by Ha Jin, these poems operate between cultures, languages, and poetic traditions. The result is a generous hybrid space in which vast territory is explored adventurously: exile and immigration, personal memories and current crisis. This poetry confronts China''s fraught political history while recalling and embodying its timeless culture and landscape. The poet is plainspoken and insightful, addressing the turmoil of displacement with meditative wisdom, self-reflection, and playfulness. Ha Jin has arrived at the height of his powers as a poet; his is a brilliant mind wrestling gracefully with the pain of an uprooted life\"--Back cover", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}], "title": "A distant center / Ha Jin.", "series": [], "source": "MARC", "_version": 2, "editions": [], "metadata": {"createdDate": "2023-06-30T13:53:22.573Z", "updatedDate": "2023-06-30T13:54:00.718Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "Poetry"}], "languages": ["eng"], "indexTitle": "Distant center /", "identifiers": [{"value": "2017037820", "identifierTypeId": "c858e4f2-2b6b-4385-842b-60732ee14abb"}, {"value": "9781556594625 (hardcover)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "1556594623 (hardcover)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "9781556595219", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "1556595212", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"va