INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('4d18f449-9d0e-4039-ae26-77e29f8313d9', '{"id": "4d18f449-9d0e-4039-ae26-77e29f8313d9", "hrid": "in00000000018", "tags": {"tagList": []}, "notes": [{"note": "\"Two of the twentieth century''s greatest art provocateurs, avant-gardists, and bohemians finally connect in the poetry of Suzanne Zelazo. Modernist poet-painters Mina Loy and Baroness Elsa von Freytag-Loringhoven had many friends in common (including Djuna Barnes and Marcel Duchamp), yet there is no record that the two ever befriended one another. Their non-relationship presents a curious \"absent presence\" in modernist history. Zelazo weaves lines of poetry by both women into an imaginary conversation, exploring the way their work has been suppressed, stitched, spliced, and edited by male editors and arbiters of taste.\"-- Provided by publisher", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}, {"note": "Includes bibliographical references (page 85)", "staffOnly": false, "instanceNoteTypeId": "86b6e817-e1bc-42fb-bab0-70e7547de6c1"}], "title": "Lances all alike / Suzanne Zelazo.", "series": [], "source": "MARC", "_version": 2, "editions": ["First edition."], "metadata": {"createdDate": "2023-06-30T13:53:22.600Z", "updatedDate": "2023-06-30T13:54:03.001Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "Poetry"}], "languages": ["eng"], "indexTitle": "Lances all alike /", "identifiers": [{"value": "1552453626 (paperback)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "9781552453629 (paperback)", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "(OCoLC)1006302612", "identifierTypeId": "439bfbae-75bc-4f74-9fc7-b2a2d47ce3ef"}], "publication": [{"role": "Publication", "place": "Toronto", "publisher": "Coach House Books", "dateOfPublication": "[2018]"}], "contributors": [{"name": "Ze