INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('ef8fda2d-3fbf-4da2-9bcc-7ba2ce5c76c8', '{"id": "ef8fda2d-3fbf-4da2-9bcc-7ba2ce5c76c8", "hrid": "in00000000057", "tags": {"tagList": []}, "notes": [{"note": "Sor Juana Ines de la Cruz (1651-1695) was the most significant literary figure of the colonial period in Spanish America. ''The autos sacramentales'', or Eucharistic plays are some of her least studied, and most perplexing works. While one of them, El divino Narciso, has received substantial scholarly attention, the other two, El cetro de Jose and El martir del Sacramento, San Hermenegildo, have been critically neglected in Sor Juana studies. This study presents a full-length analysis of all three plays, along with their loas, or the introductory pieces alongside which they were intended to be performed. Furthermore, the study seeks to place these works in their philosophical and cultural context by exploring their engagement both with orthodox Catholic sacramental theology, and the emergence of empiricism and the New Philosophy across the Hispanic world", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}, {"note": "Includes bibliographical references (pages 167-175) and index", "staffOnly": false, "instanceNoteTypeId": "86b6e817-e1bc-42fb-bab0-70e7547de6c1"}], "title": "The autos sacramentales of Sor Juana Ines de la Cruz : natural philosophy and sacramental theology / Alice Brooke.", "series": [{"value": "Oxford modern languages and literature monographs."}], "source": "MARC", "_version": 2, "editions": [], "metadata": {"createdDate": "2023-06-30T13:54:07.732Z", "updatedDate": "2023-06-30T13:54:35.034Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "Juana Ines de la Cruz, Sister, 1651-1695--Criticism and interpretation"}], "languages": ["eng"], "indexTitle": "Autos sacramentales of Sor Juana Ines de la Cruz : natural philoso