INSERT INTO oaitest_mod_inventory_storage.instance VALUES ('b25dd135-cfef-412a-978e-004609eed184', '{"id": "b25dd135-cfef-412a-978e-004609eed184", "hrid": "in00000000063", "tags": {"tagList": []}, "notes": [{"note": "Includes bibliographical references (pages 255-271) and index", "staffOnly": false, "instanceNoteTypeId": "86b6e817-e1bc-42fb-bab0-70e7547de6c1"}, {"note": "New York City''s Lower East Side, long viewed as the space of what Jacob Riis notoriously called the \"other half,\" was also a crucible for experimentation in photography, film, literature, and visual technologies. This book takes an unprecedented look at the practices of observation that emerged from this critical site of encounter, showing how they have informed literary and everyday narratives of America, its citizens, and its possible futures. Taking readers from the mid-nineteenth century to the present, Sara Blair traces the career of the Lower East Side as a place where image-makers, writers, and social reformers tested new techniques for apprehending America--and their subjects looked back, confronting the means used to represent them. This dynamic shaped the birth of American photojournalism, the writings of Stephen Crane and Abraham Cahan, and the forms of early cinema. During the 1930s, the emptying ghetto opened contested views of the modern city, animating the work of such writers and photographers as Henry Roth, Walker Evans, and Ben Shahn. After World War II, the Lower East Side became a key resource for imagining poetic revolution, as in the work of Allen Ginsberg and LeRoi Jones, and exploring dystopian futures, from Cold War atomic strikes to the death of print culture and the threat of climate change. How the Other Half Looks reveals how the Lower East Side has inspired new ways of looking-and looking back-that have shaped literary and popular expression as well as American modernity", "staffOnly": false, "instanceNoteTypeId": "10e2e11b-450f-45c8-b09b-0f819999966e"}], "title": "How the other half looks : the Lower East Side and the afterlives of images.", "series": [], "source": "MARC", "_version": 2, "editions": [], "metadata": {"createdDate": "2023-06-30T13:54:11.440Z", "updatedDate": "2023-06-30T13:54:40.174Z", "createdByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81", "updatedByUserId": "fb050af3-2bee-5452-85cb-d0eff59a5b81"}, "subjects": [{"value": "Lower East Side (New York, N.Y.)--Social life and customs--20th century"}, {"value": "Art--New York (State)--New York"}, {"value": "Documentary photography--New York (State)--New York"}, {"value": "Street photography--New York (State)--New York"}, {"value": "Slums--New York (State)--New York"}, {"value": "Inner cities--New York (State)--New York"}, {"value": "Photography, Artistic"}], "languages": ["eng"], "indexTitle": "How the other half looks : the Lower East Side and the afterlives of images.", "identifiers": [{"value": "0691172226", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "9780691172224", "identifierTypeId": "8261054f-be78-422d-bd51-4ed9f33c3422"}, {"value": "(OCoLC)1005112205", "identifierTypeId": "439bfbae-75bc-4f74-9fc7-b2a2d47ce3ef"}], "publication": [{"role": "Publication", "place": "Princeton", "publisher": "Princeton University Press", "dateOfPublication": "[2018]"}, {"dateOfPublication": "짤2018"}], "contributors": [{"name": "Blair, Sara,", "primary": true, "contributorTypeText": "author.", "contributorNameTypeId": "2b94c631-fca9-4892-a730-03ee529ffe2a"}], "instanceTypeId": "6312d172-f0cf-40f6-b27d-9fa8feaf332f", "previouslyHeld": false, "classifications": [{"classificationNumber": "F128.68.L6 B55 2018", "classificationTypeId": "ce176ace-a53e-4b4d-aa89-725ed7b2edac"}, {"classificationNumber": "970.980", "classificationTypeId": "42471af9-7d25-4f3a-bf78-60d29dcf463b"}], "instanceFormats": [], "electronicAccess": [], "holdingsRecords2": [], "modeOfIssuanceId": "9d18a02f-5897-4c31-9106-c9abb5c7ae8b", "publicationRange": [], "alternativeTitles": [], "di