INSERT INTO oaitest_mod_source_record_storage.marc_records_lb VALUES ('2b83ac74-7af8-49d0-a451-4cba75be8fb9', '{"fields": [{"001": "in00000000050"}, {"008": "180202s2018    ksua     b    001 0 eng  "}, {"005": "20230630135430.1"}, {"010": {"ind1": " ", "ind2": " ", "subfields": [{"a": "  2018004096"}]}}, {"040": {"ind1": " ", "ind2": " ", "subfields": [{"a": "DLC"}, {"b": "eng"}, {"e": "rda"}, {"c": "DLC"}, {"d": "OCLCF"}, {"d": "OCLCO"}, {"d": "AZU"}, {"d": "NhCcYBP"}]}}, {"020": {"ind1": " ", "ind2": " ", "subfields": [{"a": "9780700626281"}, {"q": "hardcover"}]}}, {"020": {"ind1": " ", "ind2": " ", "subfields": [{"a": "070062628X"}, {"q": "hardcover"}]}}, {"020": {"ind1": " ", "ind2": " ", "subfields": [{"z": "9780700626298"}, {"q": "electronic book"}]}}, {"035": {"ind1": " ", "ind2": " ", "subfields": [{"a": "(OCoLC)1012690639"}]}}, {"042": {"ind1": " ", "ind2": " ", "subfields": [{"a": "pcc"}]}}, {"043": {"ind1": " ", "ind2": " ", "subfields": [{"a": "n-us---"}]}}, {"050": {"ind1": "0", "ind2": "0", "subfields": [{"a": "E468.9"}, {"b": ".S857 2018"}]}}, {"082": {"ind1": "0", "ind2": "0", "subfields": [{"a": "973.7/1"}, {"2": "23"}]}}, {"084": {"ind1": " ", "ind2": " ", "subfields": [{"a": "HIS036050"}, {"a": "HIS036040"}, {"a": "HIS027000"}, {"2": "bisacsh"}]}}, {"100": {"ind1": "1", "ind2": " ", "subfields": [{"a": "Steplyk, Jonathan M.,"}, {"e": "author."}]}}, {"245": {"ind1": "1", "ind2": "0", "subfields": [{"a": "Fighting means killing :"}, {"b": "Civil War soldiers and the nature of combat /"}, {"c": "Jonathan M. Steplyk."}]}}, {"264": {"ind1": " ", "ind2": "1", "subfields": [{"a": "Lawrence, Kansas :"}, {"b": "University Press of Kansas,"}, {"c": "[2018]"}]}}, {"300": {"ind1": " ", "ind2": " ", "subfields": [{"a": "x, 294 pages ;"}, {"c": "24 cm"}]}}, {"336": {"ind1": " ", "ind2": " ", "subfields": [{"a": "text"}, {"b": "txt"}, {"2": "rdacontent"}]}}, {"337": {"ind1": " ", "ind2": " ", "subfields": [{"a": "unmediated"}, {"b": "n"}, {"2": "rdamedia"}]}}, {"338": {"ind1": " ", "ind2": " ", "subfields": [{"a": "volume"}, {"b": "nc"}, {"2": "rdacarrier"}]}}, {"520": {"ind1": " ", "ind2": " ", "subfields": [{"a": "\"War means fighting, and fighting means killing.\" Confederate cavalry commander Nathan Bedford Forrest famously declared. The Civil War was fundamentally a matter of Americans killing Americans. This undeniable reality is what Jonathan Steplyk explores in Fighting Means Killing, the first book-length study of Union and Confederate soldiers'' attitudes toward, and experiences of, killing in the Civil War. Drawing upon letters, diaries, and postwar reminiscences, Steplyk examines what soldiers and veterans thought about killing before, during, and after the war. How did these soldiers view sharpshooters? How about hand-to-hand combat? What language did they use to describe killing in combat? What cultural and societal factors influenced their attitudes? And what was the impact of race in battlefield atrocities and bitter clashes between white Confederates and black Federals? These are the questions that Steplyk seeks to answer in Fighting Means Killing, a work that bridges the gap between military and social history--and that shifts the focus on the tragedy of the Civil War from fighting and dying for cause and country to fighting and killing. \"--"}, {"c": "Provided by publisher."}]}}, {"520": {"ind1": " ", "ind2": " ", "subfields": [{"a": "\"War means fighting, and fighting means killing,\" Confederate cavalry commander Nathan Bedford Forrest famously declared. At its core the Civil War involved Americans killing Americans. Regardless of the moral lens through which one views the conflict, that truth remains. Relying heavily on the wartime letters and diaries and postwar reminiscences of soldiers and veterans, Jonathan Steplyk explores Union and Confederate attitudes about, and experiences of, killing in combat, and contends that the majority of soldiers positively affirmed and accepted killing the enemy as part of their military duty and a necessity for their respective causes to prevail. Steplyk examines the cultural and societal factors that influenced soldiers'' attitudes toward killing prior to and during the war, the various ways soldiers experienced killing in battle, the terms and turns of phrase that soldiers used to describe killing in combat, killing that transgressed the laws of war, and the impact of race and racial attitudes on killing. Fighting Means Killing is the first book-length treatment of the nature of killing in Civil War combat, and bridges the gap between military and social history\"--"}, {"c": "Provided by publisher."}]}}, {"504": {"ind1": " ", "ind2": " ", "subfields": [{"a": "Includes bibliographical references and index."}]}}, {"505": {"ind1": "0", "ind2": " ", "subfields": [{"a": "The things they carried: influences on Civil War soldiers and killing -- Seeing the elephant: killing and the face of battle -- Good execution: the language of killing -- With bayonet and clubbed musket: killing in hand-to-hand combat -- Murder and mercy: the extremes of killing -- Killing in black and white: race, combat, and hate."}]}}, {"611": {"ind1": "2", "ind2": "7", "subfields": [{"a": "American Civil War (1861-1865)"}, {"2": "fast"}, {"0": "(OCoLC)fst01351658"}]}}, {"651": {"ind1": " ", "ind2": "0", "subfields": [{"a": "United States"}, {"x": "History"}, {"y": "Civil War, 1861-1865"}, {"x": "Moral and ethical aspects."}]}}, {"651": {"ind1": " ", "ind2": "0", "subfields": [{"a": "United States"}, {"x": "History"}, {"y": "Civil War, 1861-1865"}, {"x": "Psychological aspects."}]}}, {"650": {"ind1": " ", "ind2": "0", "subfields": [{"a": "Combat"}, {"x": "Psychological aspects"}, {"x": "History"}, {"y": "19th century."}]}}, {"650": {"ind1": " ", "ind2": "0", "subfields": [{"a": "Soldiers"}, {"z": "United States"}, {"x": "Psychology"}, {"x": "History"}, {"y": "19th century."}]}}, {"650": {"ind1": " ", "ind2": "7", "subfields": [{"a": "HISTORY / United States / 