CREATE OR REPLACE FUNCTION oaitest_mod_inventory_storage.strtotimestamp(
	text)
    RETURNS timestamp with time zone
    LANGUAGE 'sql'
    COST 100
    IMMUTABLE STRICT PARALLEL UNSAFE
AS $BODY$
SELECT $1::timestamptz
$BODY$;

ALTER FUNCTION oaitest_mod_inventory_storage.strtotimestamp(text)
    OWNER TO folio_admin;

CREATE OR REPLACE FUNCTION oaitest_mod_inventory_storage.dateormin(
   timestamp with time zone)
   RETURNS timestamp with time zone
   LANGUAGE 'sql'
   COST 100
   IMMUTABLE PARALLEL UNSAFE
AS $BODY$
SELECT COALESCE($1, timestamptz '1970-01-01')
$BODY$;

ALTER FUNCTION oaitest_mod_inventory_storage.dateormin(timestamp with time zone)
   OWNER TO folio_admin;

